void solve()
{
    long long n;
    cin >> n;
    long long sum_total = ((n + 1) * (n + 1)) / 4;
    long long low = 0, high = 1e9 + 1, s = 0;
    while (low <= high) {
        long long mid = (low + high) / 2;
        if (mid * mid <= sum_total) {
            s = mid;
            low = mid + 1;
        } else {
            high = mid - 1;
        }
    }
    cout << s << '\n';
}
