vector<Node> tmp;
while (!sk_b.empty() && sk_b.top().c == segr[i].c) {
    tmp.push_back(sk_b.top());
    sk_b.pop();
}
if (!sk_b.empty()) {
    ans[segr[i].id] = min(ans[segr[i].id], max(0ll, segr[i].l - sk_b.top().pos));
}
for (auto &x : tmp) {
