          #include <bits/stdc++.h>
          using namespace std;
          #define int long long

          const int N = 2e5;

          int n, m, a[N], ans;

          int f(int i) {
              return i * (i+1) / 2;
          }

          signed main() {
              ios::sync_with_stdio(0); cin.tie(0);
              cin >> n >> m;
              ans = f(n);
              for (int i = 0; i < n; i++) {
                  cin >> a[i];
              }
              for (int i = 0; i < n-1; i++) {
                  if (a[i] ! = a[i+1]) {
                      ans += f(n) - f(i+1) - f(n-i-1);
                  }
              }
              while (m--) {
                  int i, x;
                  cin >> i >> x;
                  i--;
                  for (int j = i-1; j <= i; j++) {
                      if (j < 0 || j >= n-1) continue; if (a[j] ! = a[j+1]) {
                          ans -= f(n) - f(j+1) - f(n-j-1);
                      }
                  }
                  a[i] = x;
                  for (int j = i-1; j <= i; j++) {
                      if (j < 0 || j >= n-1) continue;
                      if (a[j] ! = a[j+1]) {
                          ans += f(n) - f(j+1) - f(n-j-1);
                      }
                  }
                  cout << ans << '\n';
              }
          }

