      #include <bits/stdc++.h>
      using namespace std;
      typedef long long ll;

      const int N = 100000;
      ll a[N], p[N];
      int n;

      struct Fenw {
          vector<ll> tree;
          void init(int n) {
              tree.assign(n+1, 0);
          }
          void update(int i, ll v) {
              for (; i < tree.size(); i += i & -i) tree[i] += v;
          }
          ll query(int i) {
              ll res = 0;
              for (; i; i -= i & -i) res += tree[i];
              return res;
          }
      } fenw;

      void solve() {
          cin >> n;
          for (int i = 0; i < n; i++) {
              cin >> a[i];
          }
          p[0] = a[0];
          for (int i = 1; i < n; i++) {
              p[i] = p[i-1] + a[i];
          } if (p[n-1] != *max_element(p, p+n) || *min_element(p, p+n) < 0) {
              cout << "-1\n";
              return;
          }
          vector<ll> temp;
          for (int i = 0; i < n-1; i++) { // note: only the first n-1
              temp.push_back(p[i]);
          }
          sort(temp.begin(), temp.end());
          temp.erase(unique(temp.begin(), temp.end()), temp.end());
          fenw.init(n);
          ll ans = 0;
          for (int i = n-2; i >= 0; i--) {
              int pos = lower_bound(temp.begin(), temp.end(), p[i]) - temp.begin() + 1;
              ans += fenw.query(pos-1);
              fenw.update(pos, 1);
          }
          cout << ans << '\n';
      }

      int main() {
