            #include <bits/stdc++.h>
            using namespace std;
            const int N = 1e5+5;
            int n, f[N];
            vector<int> g[N];
            void dfs(int u) {
                if (g[u].empty()) {
                    f[u] = 1;
                    return;
                }
                for (int v : g[u]) {
                    dfs(v);
                    f[u] = max(f[u], f[v]);
                }
                for (int v : g[u]) {
                    if (f[u] == f[v]) {
                        f[u]++;
                        break;
                    }
                }
            }
            int main() {
                cin >> n;
                for (int i = 2; i <= n; i++) {
                    int p; cin >> p;
                    g[p].push_back(i);
                }
                dfs(1);
                cout << f[1] << endl;
            }

