                #include <bits/stdc++.h>
                using namespace std;
                typedef long long ll;
                const int maxn = 2e5+5;
                ll a[maxn],pre[maxn];
                int main() {
                    int n,q; 
                    cin >> n >> q;
                    for(int i=1;i<=n;++i) cin >> a[i];
                    sort(a+1,a+1+n,greater<ll>());
                    for(int i=1;i<=n;++i) pre[i] = pre[i-1] + a[i];
                    while(q--) {
                        int x,y;
                        cin >> x >> y;
                        cout << pre[x] - pre[x-y] << '\n';
                    }
                    return 0;
                } This solution: 
                sorted in descending order. prefix[0]=0, then for i from 1 to n: prefix[i] = prefix[i-1]+a[i]

                for a query (x,y): output = pre[x] - pre[x-y]

