            #include <bits/stdc++.h>
            using namespace std;
            int main() {
                ios::sync_with_stdio(false);
                cin.tie(nullptr);
                int t;
                cin >> t;
                while (t--) {
                    int n;
                    cin >> n;
                    vector<int> a(n);
                    for (int i = 0; i < n; i++) {
                        cin >> a[i];
                    }
                    int l = 0, r = n - 1;
                    int ans = 0;
                    long long left_sum = 0, right_sum = 0;
                    while (l <= r) {
                        if (left_sum <= right_sum) {
                            left_sum += a[l];
                            l++;
                        } else {
                            right_sum += a[r];
                            r--;
                        } if (left_sum == right_sum) {
                            ans = l + (n - 1 - r);
                        }
                    }
                    cout << ans << '\n';
                }
            }

