    int n;
    cin >> n;
    vector<long long> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }
    long long s1 = 0, s2 = 0;
    int i = 0, j = n - 1;
    int ans = 0;
    while (i <= j) {
        if (s1 < s2) {
            s1 += a[i];
            i++;
        } else if (s2 < s1) {
            s2 += a[j];
            j--;
        } else {
            ans = i + (n - 1 - j);
            s1 += a[i];
            i++;
