              if (l_sum <= r_sum) {
                  l_sum += a[l++];
              } else {
                  r_sum += a[r--];
              } if (l_sum == r_sum) {
                  ans = l + (n - 1 - r);
              }
          }
          cout << ans << endl;
      }
  }

