const int N = 400000;
const int mod = 1e9+7;
int f[N+10], inv[N+10];
int C(int n, int m) {
    if (m < 0 || m > n) return 0;
    return 1LL * f[n] * inv[m] % mod * inv[n-m] % mod;
}
int main() {
    f[0] = 1;
    for (int i=1; i<=N; i++) f[i] = 1LL * f[i-1] * i % mod;
