    // wait, inv[N] should be the modular inverse of f[N]
    // usually: 
    //   inv[N] = pow(f[N], mod-2, mod);
    //   for (int i=N-1; i>=0; i--) inv[i] = 1LL * inv[i+1] * (i+1) % mod;
    // but the above is for inv of factorial, not for inv of i.

    // Actually, the intended precomputation for inv is for factorials.

    int n; cin >> n;
    vector<int> a(n+1);
    for (int i=0; i<=n; i++) cin >> a[i];

    long long ans = 0;
    for (int i=0; i<=n; i++) {
        if (a[i] > 0) {
            ans = (ans + C(a[i]+i, i+1)) % mod;
        }
    }
    cout << ans << endl;
}

