                    int ans = 0;
                    while (max_cnt < n) {
                        ans++;
                        int d = min(n - max_cnt, max_cnt);
                        ans += d;
                        max_cnt += d;
                    }
                    cout << ans << '\n';
                }
                return 0;
            }

