          #include <bits/stdc++.h>
          using namespace std;

          int t;
          string s;

          int main() {
            ios_base::sync_with_stdio(false);
            cin.tie(NULL);
            cin >> t;
            while (t--) {
              int n; 
              cin >> n;
              cin >> s;
              bool valid = true;
              // We'll split by W
              int i = 0;
              while (i < n) {
                // Skip W
                while (i < n && s[i]=='W') i++;
                if (i==n) break;
                int j = i;
                int r=0, b=0;
                while (j<n && s[j]!='W') {
                  if (s[j]=='R') r++;
                  else b++;
                  j++;
                }
                // The contiguous non-W segment is from i to j-1
                if (r==0 || b==0) {
                  valid = false;
                  break;
                }
                i = j;
              }
              if (valid) cout << "YES\n";
              else cout << "NO\n";
            }
          }

