                #include <bits/stdc++.h>
                using namespace std;
                typedef long long ll;
                const int N = 300000;
                const int MOD = 998244353;
                int n;
                vector<int> add[N+10], del[N+10];
                int pw2[N+10], pw3[N+10];

                struct Heap {
                    priority_queue<int, vector<int>, greater<int>> q, p;
                    void push(int x) { q.push(x); }
                    void erase(int x) { p.push(x); }
                    int top() {
                        while (!p.empty() && q.top()==p.top()) { q.pop(); p.pop(); }
                        return q.top();
                    }
                } h;

                int main() {
                    scanf("%d", &n);
                    for (int i=0; i<n; i++) {
                        int l, r;
                        scanf("%d%d", &l, &r);
                        add[l].push_back(i);
                        del[r+1].push_back(i);
                    }

                    pw2[0] = 1;
                    for (int i=1; i<=n; i++) pw2[i] = pw2[i-1]*2 % MOD;
                    pw3[0] = 1;
                    for (int i=1; i<=n; i++) pw3[i] = (ll)pw3[i-1]*3 % MOD;

                    ll ans = 0;
                    set<int> active;
                    for (int i=0; i<=N; i++) {
                        for (int j: del[i]) {
                            active.erase(j);
                            h.erase(j);
                        }
                        for (int j: add[i]) {
                            active.insert(j);
                            h.push(j);
                        }
                        if (active.empty()) continue;
                        int min_active = h.top();
                        int k = min_active;
                        int left = n - 1 - k;
                        int right = k;
                        ans = (ans + (ll)pw3[left] * pw2[right]) % MOD;
                    }
                    printf("%lld\n", ans);
                }

